
public class Colour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String colour = "yellow";
		
		switch(colour) {
		
		case "violet" : System.out.println("violet is present in rainbow colours");
						break;
		case "indigo" : System.out.println("indigo is present in rainbow colours");
						break;
		case "blue"   : System.out.println("blue is present in rainbow colours");
						break;
		case "gray"   : System.out.println("gray is present in rainbow colours");
						break;
		case "yellow" : System.out.println("yellow is present in rainbow colours");
						break;
		case "orange" : System.out.println("orange is present in rainbow colours");
						break;
		case "red"    : System.out.println("red is present in rainbow colours");
						break;
		default		  : System.out.println("entered colour is not in rainbow");
						break;				
						
		}
	}

}
